import Phaser from 'phaser';
import { GameController } from '../../GameController';
import { GameInfo } from 'tr-common';
import { NumberOfStartingPointsExceeded } from '../../../../back-end/src/game/errors';
const addGameForm = require('../../../assets/form/add_game.html.template');

const MAX_GAMES_DISPLAYED = 10;

class GameSlot {
    text: Phaser.GameObjects.Text;
    gameId: string;

    constructor(text: Phaser.GameObjects.Text, gameId: string) {
        this.text = text;
    }
}

export class ListGames extends Phaser.Scene
{
    gameSlots: GameSlot[] = [];
    controller: GameController;
    currentPage: number = 0;
    createdGames: number = 0; 
    form: Phaser.GameObjects.DOMElement;

    init(controller: GameController) {
        this.controller = controller;
    }

    preload() {
        this.load.html('add_game', addGameForm);
    }

    create() {
        this.createSlots();
        this.setupForm();
    }

    createSlots() 
    {
        let y = 100;
        const x = 20;
        const hegiht = 40;

        const textConfig = {
            fontFamily: 'Verdana, "Times New Roman", Tahoma, serif',
            fontSize: 40
        }

        for(let i = 0; i < MAX_GAMES_DISPLAYED; i++) {

            let gameName = "PUSTE";
            let gameId = undefined

            const gameText = this.add.text(x, y, gameName, textConfig);

            gameText.setInteractive();
            gameText.on('pointerover', () => gameText.setFill("#FF0000"));
            gameText.on('pointerout', () => gameText.setFill('#FFFFFF'));
            gameText.on('pointerdown', () => {
                const gameId = this.gameSlots[i].gameId;
                this.controller.join(gameId);
            })

            this.gameSlots.push(new GameSlot(gameText, gameId));
            y += hegiht;
        }
    }

    setGames(games: GameInfo[]) {        

        if (games.length === 10) {
            this.form.destroy();
        }

        const end = Math.min(games.length, MAX_GAMES_DISPLAYED);

        for (let i = 0; i < end; i++) {
            const slot = this.gameSlots[i];
            const game = games[i];

            slot.gameId = game.id;
            slot.text.text = game.name; 
        }

    }

    private setupForm() 
    {
        const controller = this.controller;
        this.form = this.add.dom(400, 50).createFromCache('add_game');
        this.form.addListener('click');
        this.form.on('click', function (event: any) {
            if (event.target.name !== 'add') {
                return;
            }

            const gameInput = this.getChildByName('game-name');
            controller.addGame(gameInput.value);
        })
    }

    update() {
        const games = this.controller.state.games;

        let gameIndex = this.currentPage * MAX_GAMES_DISPLAYED;

        for (let i = 0; i < MAX_GAMES_DISPLAYED; i++) {
            const slot = this.gameSlots[i];
            const game = games[gameIndex++];

            if (!game) {
                slot.gameId = undefined;
                slot.text.text = "PUSTE"
                continue;
            }

            slot.gameId = game.id;
            slot.text.text = game.name;
        }
    }
}
